  city: 'Flavortown',
  state: 'NY',
  zipCode: '10101',
  category: 'Italian',
  rating: 4.5,
  reviewCount: 90
};
const businesses = [
  business,
  business,
  business,
  business,
  business,
  business
];*/
;
class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      businesses:[]
    };
    this.searchYelp = this.searchYelp.bind(this);
  }
  searchYelp(term, location, sortBy) {
    Yelp.search(term, location, sortBy).then( businesses =>{
      this.setState({
        businesses: this.businesses
      })
    })
  }
  render() {
    return (
      <div className="App">
        <h1>ravenous</h1>
        <SearchBar searchYelp={this.searchYelp} />
        <BusinessList businesses={businesses} />
      </div>
    );
  }
}
export default App
